angular.module("capitales",[]).controller("GameController",["$scope","$http",function(a,b){var c=this;c.step1=!0,c.step2=!1,c.step3=!1,c.startGame=function(){b.put("/capitales/service/game/"+c.size+"/"+c.username).success(function(a,b,d,e){c.step1=!1,c.step2=!0,c.step3=!1,c.nextQuestion()})},c.giveAnswer=function(){b.get("/capitales/service/answer/"+c.answer).success(function(a,b,d,e){c.proposition=a,c.nextQuestion()})},c.nextQuestion=function(){b.get("/capitales/service/next").success(function(a,b,d,e){"undefined"==typeof a.value?(c.step1=!1,c.step2=!1,c.step3=!0):c.question=a.value})}}]).directive("gmEnter",function(){return function(a,b,c){b.bind("keydown keypress",function(b){13===b.which&&(a.$apply(function(){a.$eval(c.gmEnter)}),b.preventDefault())})}});